// A map to store base64 encoded audio data
const sounds: { [key: string]: string } = {
  pop: 'data:audio/wav;base64,'
};
// A map to cache decoded AudioBuffers
const audioBuffers: { [key: string]: AudioBuffer } = {};
const audioContext = new (window.AudioContext || (window as any).webkitAudioContext)();

export const playSound = async (name: string) => {
  if (!audioContext) return;
  
  if (audioContext.state === 'suspended') {
    await audioContext.resume();
  }
  
  if (audioBuffers[name]) {
    const source = audioContext.createBufferSource();
    source.buffer = audioBuffers[name];
    source.connect(audioContext.destination);
    source.start(0);
    return;
  }
  
  const src = sounds[name];
  if (!src) {
      console.error(`Sound not found: ${name}`);
      return;
  }

  try {
    const response = await fetch(src);
    const arrayBuffer = await response.arrayBuffer();
    const audioBuffer = await audioContext.decodeAudioData(arrayBuffer);
    audioBuffers[name] = audioBuffer; // Cache it for future use
    
    const source = audioContext.createBufferSource();
    source.buffer = audioBuffer;
    source.connect(audioContext.destination);
    source.start(0);
  } catch (error) {
    console.error(`Unable to decode audio data: ${name}`, error);
  }
};